import React, { useState , useRef } from 'react';



const Uploadcon = () => {
    const [trainingDataset, setTrainingDataset] = useState(null);
    const [validationDataset, setValidationDataset] = useState(null);
    const [message, setMessage] = useState('');


    const trainingFileRef =useRef();
    const validationFileRef =useRef();


    const handleTrainingDatasetUpload = (event) => {
        const file = event.target.files[0];
        if (file.type !== 'application/zip' && file.type !== 'application/x-zip-compressed') {
            setMessage('Invalid File Type! Please upload a zip file.');
            return;
        }
        setTrainingDataset(file);

    };

    const handleValidationDatasetUpload = (event) => {
        const file = event.target.files[0];
        setValidationDataset(file);
    };

    const handleSubmit = () => {
        if (!trainingDataset || !validationDataset) {
            setMessage('Please upload both training and validation datasets!');
            return;
        }
        // Perform actions with the uploaded datasets
        console.log('Training Dataset:', trainingDataset);
        console.log('Validation Dataset:', validationDataset);

        setMessage('Datasets Upload successful!');
    };

    const handleReset = () => {
        setTrainingDataset(null);
        setValidationDataset(null);
        setMessage('Clearing datasets...');
        if (trainingFileRef.current) {
            trainingFileRef.current.value = '';
        }
        if (validationFileRef.current) {
            validationFileRef.current.value = '';
        }
    }

    return (
        <div>
            <h1 className='text-2xl font-bold mb-4'>Upload Training and Validation Datasets</h1>
            <div className='flex justify-between'>
                <div>
                    <h2 className ="text-xl font-semibold mb-2">Training Dataset</h2>
                    <input type="file" accept=".zip" onChange={handleTrainingDatasetUpload} />
                </div>
                <div>
                    <h2 classnName ="text-xl font-semibold mb-2">Validation Dataset</h2>
                    <input type="file" accept=".zip" onChange={handleValidationDatasetUpload} />
                </div>
            </div>
            <button onClick={handleSubmit} className="bg-blue-500 text-white p-2 rounded mt-4">Upload</button>
            <button onClick={handleReset} className="bg-red-500 text-white p-2 rounded mt-4 ml-2">Reset</button> {/* Reset the datasets */}
            {message && <p>{message}</p>} {/* Display message if not null */}
        </div>
    );
};

export default Uploadcon;
