import React, { useState } from 'react';

const Uploadcon = () => {
    const [trainingDataset, setTrainingDataset] = useState(null);
    const [validationDataset, setValidationDataset] = useState(null);

    const handleTrainingDatasetUpload = (event) => {
        const file = event.target.files[0];
        setTrainingDataset(file);
    };

    const handleValidationDatasetUpload = (event) => {
        const file = event.target.files[0];
        setValidationDataset(file);
    };

    const handleSubmit = () => {
        // Perform actions with the uploaded datasets
        console.log('Training Dataset:', trainingDataset);
        console.log('Validation Dataset:', validationDataset);
    };

    return (
        <div>
            <h1>Upload Training and Validation Datasets</h1>
            <input type="file" accept=".zip" onChange={handleTrainingDatasetUpload} />
            <input type="file" accept=".zip" onChange={handleValidationDatasetUpload} />
            <button onClick={handleSubmit}>Upload</button>
        </div>
    );
};

export default Uploadcon;
